import React from 'react';
import './StartMenu.css';

const StartMenu = ({show, onStart, gameOver, setQuestionNumber, score, total}) => {

	return (
		gameOver ? 
			<div className="menu-end">
				<div className="score">
					<h2>You scored {score}/{total}</h2>
				</div>
				<div className="restart">
					<button onClick={() => onStart(!show)}>Play Again?</button>
				</div>
			</div> :
			<div className="menu">
				<div className="heading">
					<h1>Sports Trivia</h1>
				</div>
				<div className="start">
					<button onClick={() => onStart(!show)}>Start</button>
				</div>
			</div>
	);
};

export default StartMenu;
