import { combineReducers } from 'redux';

const initialQuestionState = {
    questions: []
};

const questionReducer = (state=initialQuestionState, action) => {
    switch(action.type) {
        case 'FETCH_QUESTIONS':
            const questions = action.payload.map((question) => {
                const formattedQuestions = {
                    question: question.question
                }
                const answerChoices = [...question.incorrect_answers];
                formattedQuestions.answer = Math.floor(Math.random() * 3);
                answerChoices.splice(formattedQuestions.answer, 0, question.correct_answer);
                formattedQuestions.choices = [];
                answerChoices.forEach((choice, index) => {
                    formattedQuestions.choices.push(choice);
                })
                return formattedQuestions;
            });
            return {...state, questions: questions};
        default:
            return state;
    };
};

const initialState = {
    start: false,
    gameOver: false,
    select: false,
    questionIndex: 0,
    score: 0
}

const startReducer = (state=initialState, action) => {
    switch(action.type) {
        case 'START_GAME':
            return {...state, start: true };
        default:
            return state;
    };

};

const gameOverReducer = (state=initialState, action) => {
    switch(action.type) {
        case 'START_GAME':
            return {...state, gameOver: true };
        default:
            return state;
    };

};

const selectReducer = (state=initialState, action) => {
    switch(action.type) {
        case 'CHOICE_SELECTED':
            return {...state, select: true };
        default:
            return state;
    };

};

const questionIndexReducer = (state=initialState, action) => {
    switch(action.type) {
        case 'NEXT_QUESTION':
            return {...state, questionIndex: state.questionIndex + 1 };
        default:
            return state;
    };

};

const incrementScoreReducer = (state=initialState, action) => {
    switch(action.type) {
        case 'INCREMENT_SCORE':
            return {...state, score: state.score + 1 };
        default:
            return state;
    };

};

export default combineReducers({
    questionReducer, 
    startReducer, 
    gameOverReducer,
    selectReducer,
    questionIndexReducer,
    incrementScoreReducer
});
