// import './StartMenu.css';

const StartMenu = ({gameOver, score, total, startGame}) => {

	// //resetting the game - score and question number back to 0
	// restart = () => {
	// 	// this.props.fetchQuestions();
	// 	onStart(!show);
	// 	resetScore(0);
	// 	setQuestionNumber(0);
	// }

//displaying user's score and giving them the option to restart the game
	return (
		gameOver ? 
			<div className="menu-end">
				<div className="score">
					<h2>You scored {score}/{total}</h2>
				</div>
				<div className="restart">
					<button onClick={() => console.log('hi')}>Play Again?</button>
				</div>
			</div> :
			<div className="menu">
				<div className="heading">
					<h1>Trivia</h1>
				</div>
				<div className="start">
					<button onClick={() => startGame()}>Start</button>
				</div>
			</div>
	);
};

export default StartMenu;
