import React, {useRef} from 'react';
import NextQuestion from './NextQuestion';
import './QuestionDisplay.css';

const QuestionDisplay = ({questions, next, select, onCorrect, questionIndex, length}) => {
	const refs = useRef([]);

	//looping through answer choices and checking them against the user's selected choice
	const checkAnswer = event => {
		for (let i = 0; i < question.length; i++) {
			if (event.target.textContent === questionDetails.incorrect_answers[i]) {
				refs.current[i].setAttribute("style", "background-color: red");
			}
			else if (event.target.textContent === questionDetails.correct_answer) {
				refs.current[i].setAttribute("style", "background-color: greem");
				onCorrect();
			}
		}
			onAnswerSelect(true);
			if (display.questionChoices[i].isCorrect === true) {
				refs.current[i].setAttribute("style", "background-color: green");
			}
		}
	}

	const prevent = event => {
		event.preventDefault();
	}

	const choices = () => {
		const answerChoices = [...questionDetails.incorrect_answers];


		display.questionChoices.map((choice, index) => {
		return (
			<button 
				className="choice"
				key={index}
				ref={el => refs.current.push(el)} 
				onClick={select ? prevent : checkAnswer}>{choice.answerText}
			</button>
		);
	})


	return (
		<div className="container">
			<div className="question-display">
				<div className="question-text">
					<h1>{display.questionText}</h1>
				</div>
				<div className="choice-wrapper">
					{choices}
				</div>
			</div>
			<div className="next">
				{select ? <NextQuestion next={next} questionNumber={questionNumber} length={length-1} /> : ''}
			</div>
		</div>
	);
}

export default QuestionDisplay;
