import React, {Component} from 'react';
import { connect } from 'react-redux';

import NextQuestion from './NextQuestion';
import '../style/App.scss';

class QuestionDisplay extends Component {

	// checkAnswer(event) {
	// 	let choices = document.getElementsByClassName('choice');

	// }

	renderChoices() {
		const choices = this.props.questions[this.props.questionIndex].choices.map((choice, index) => {
			return (
				<button
					dangerouslySetInnerHTML={{__html: choice}}
					className="choice"
					key={index}
					// onClick={}
				>
				</button>
			);
		})
		return choices;
	}

	render() {
		console.log(this.props.questions)
		return (
			<div className="container">
				<strong>Question: {this.props.questionIndex + 1} of {this.props.length}</strong>
				<div className="question-display">
					<div className="question-text">
						<h1 dangerouslySetInnerHTML={{__html:this.props.questions[this.props.questionIndex].question}}></h1>
					</div>
					<div className="choice-wrapper">
						{this.renderChoices()}
					</div>
				</div>
				<div className="next">
				</div>
			</div>
		);
	}
}
					// {select ? <NextQuestion next={next} questionIndex={questionIndex} length={length-1} /> : ''}

const mapStateToProps = state => {
	return {
		questionIndex: state.questionIndexReducer.questionIndex,
		questions: state.questionReducer.questions
	};
};

export default connect(mapStateToProps)(QuestionDisplay);

