import React, {useState} from 'react';
import StartMenu from './components/StartMenu';
import QuestionDisplay from './components/QuestionDisplay';

const questions = [
	{
		questionText: 'Which of the following NFL coaches has the best playoff winning percentage?',
		questionChoices: [
			{
				answerText: 'Chuck Noll',
				isCorrect: false
			},
			{
				answerText: 'Jimmy Johnson',
				isCorrect: false
			},
			{
				answerText: 'Barry Switzer',
				isCorrect: true
			},
					{
				answerText: 'Bill Belichick',
				isCorrect: false
			},
		]
	},
	{
		questionText: 'Who was the only college football player to win the Heisman Trophy twice?',
		questionChoices: [
			{
				answerText: 'O.J. Simpson',
				isCorrect: false
			},
			{
				answerText: 'Archie Griffin',
				isCorrect: true
			},
			{
				answerText: 'Jay Berwanger',
				isCorrect: false
			},
					{
				answerText: 'Earl Campbell',
				isCorrect: false
			},
		]
	},
	{
		questionText: 'What pitcher threw the only no-hit game in World Series history?',
		questionChoices: [
			{
				answerText: 'Roger Clemens',
				isCorrect: false
			},
			{
				answerText: 'Sandy Koufax',
				isCorrect: false
			},
			{
				answerText: 'Nolan Ryan',
				isCorrect: false
			},
					{
				answerText: 'Don Larsen',
				isCorrect: true
			},
		]
	},
	{
		questionText: 'What is the only city to win three major sports championships in the same year?',
		questionChoices: [
			{
				answerText: 'San Francisco',
				isCorrect: false
			},
			{
				answerText: 'New York',
				isCorrect: false
			},
			{
				answerText: 'Detroit',
				isCorrect: true
			},
					{
				answerText: 'Dallas',
				isCorrect: false
			},
		]
	},
	{
		questionText: 'With which pick in the 2011 NBA draft was Clippers Forward Kawhi Leonard selected with?',
		questionChoices: [
			{
				answerText: '15th',
				isCorrect: true
			},
			{
				answerText: '8th',
				isCorrect: false
			},
			{
				answerText: '13th',
				isCorrect: false
			},
					{
				answerText: '21st',
				isCorrect: false
			},
		]
	},
	{
		questionText: 'Who holds the record for most passing touchdowns in an NFL season?',
		questionChoices: [
			{
				answerText: 'Patrick Mahomes',
				isCorrect: false
			},
			{
				answerText: 'Tom Brady',
				isCorrect: false
			},
			{
				answerText: 'Peyton Manning',
				isCorrect: true
			},
					{
				answerText: 'Aaron Rodgers',
				isCorrect: false
			},
		]
	},
	{
		questionText: 'Who was the youngest NHL player to record a hat trick?',
		questionChoices: [
			{
				answerText: 'Connor McDavid',
				isCorrect: false
			},
			{
				answerText: 'Patrick Laine',
				isCorrect: false
			},
			{
				answerText: 'Sidney Crosby',
				isCorrect: false
			},
					{
				answerText: 'Jordan Staal',
				isCorrect: true
			},
		]
	}
];

const App = () => {
	const [start, setStart] = useState(false);
	const [select, setSelect] = useState(false);
	const [questionNumber, setQuestionNumber] = useState(0);
	const [question, setQuestion] = useState(questions[questionNumber]);
	const [gameOver, setGameOver] = useState(false);
	const [score, setScore] = useState(0);

	const nextQuestion = () => {
		const nextQ = questionNumber + 1;
		setQuestionNumber(nextQ);
		const buttons = document.getElementsByTagName('button');
		for (let i = 0; i< buttons.length; i++) {
			buttons[i].removeAttribute('style');
		}
		if (nextQ < questions.length) {
			setQuestion(questions[nextQ]);
			setSelect(false);
		} else {
			setGameOver(true);
			setStart(false);
			setQuestion(questions[0]);
			setSelect(false);
		}
	}

	const incrementScore = () => {
		setScore(score + 1);
	}

	return (
		start ? <QuestionDisplay 
							display={question} 
							next={nextQuestion} 
							select={select} 
							onAnswerSelect={setSelect}
							onCorrect={incrementScore}
							questionNumber={questionNumber}
							length={questions.length}
						/> : 
						<StartMenu 
							show={start} 
							onStart={setStart}
							gameOver={gameOver} 
							setQuestionNumber={setQuestionNumber}
							score={score}
							resetScore={setScore}
							total={questions.length}
						/>
	);
};

export default App;
