import React, { useState, useEffect } from 'react';
import StartMenu from './components/StartMenu';
import QuestionDisplay from './components/QuestionDisplay';

const questions = [
	{
		questionText: 'Which of the following NFL coaches has the best playoff winning percentage?',
		questionChoices: [
			{
				answerText: 'Chuck Noll',
				isCorrect: false
			},
			{
				answerText: 'Jimmy Johnson',
				isCorrect: false
			},
			{
				answerText: 'Barry Switzer',
				isCorrect: true
			},
					{
				answerText: 'Bill Belichick',
				isCorrect: false
			},
		]
	},
	{
		questionText: 'Who was the only college football player to win the Heisman Trophy twice?',
		questionChoices: [
			{
				answerText: 'O.J. Simpson',
				isCorrect: false
			},
			{
				answerText: 'Archie Griffin',
				isCorrect: true
			},
			{
				answerText: 'Jay Berwanger',
				isCorrect: false
			},
					{
				answerText: 'Earl Campbell',
				isCorrect: false
			},
		]
	},
	{
		questionText: 'What pitcher threw the only no-hit game in World Series history?',
		questionChoices: [
			{
				answerText: 'Roger Clemens',
				isCorrect: false
			},
			{
				answerText: 'Sandy Koufax',
				isCorrect: false
			},
			{
				answerText: 'Nolan Ryan',
				isCorrect: false
			},
					{
				answerText: 'Don Larsen',
				isCorrect: true
			},
		]
	},
	{
		questionText: 'What is the only city to win three major sports championships in the same year?',
		questionChoices: [
			{
				answerText: 'San Francisco',
				isCorrect: false
			},
			{
				answerText: 'New York',
				isCorrect: false
			},
			{
				answerText: 'Detroit',
				isCorrect: true
			},
					{
				answerText: 'Dallas',
				isCorrect: false
			},
		]
	},
	{
		questionText: 'With which pick in the 2011 NBA draft was Clippers Forward Kawhi Leonard selected with?',
		questionChoices: [
			{
				answerText: '15th',
				isCorrect: true
			},
			{
				answerText: '8th',
				isCorrect: false
			},
			{
				answerText: '13th',
				isCorrect: false
			},
					{
				answerText: '21st',
				isCorrect: false
			},
		]
	},

];

const App = () => {
	const [start, setStart] = useState(false);
	const [select, setSelect] = useState(false);
	const [questionNumber, setQuestionNumber] = useState(0);
	const [question, setQuestion] = useState(questions[questionNumber]);

	const nextQuestion = () => {
		const nextQ = questionNumber + 1;
		setQuestionNumber(nextQ);
		const buttons = document.getElementsByTagName('button');
		for (let i = 0; i< buttons.length; i++) {
			console.log(button[i]);
		}
		if (nextQ < questions.length) {
			setQuestion(questions[nextQ]);
			console.log(nextQ);
			setSelect(false);
		} else {
			console.log('done!');
		}
	}

	return (
		start ? <QuestionDisplay 
							display={question} 
							next={nextQuestion} 
							select={select} 
							onAnswerSelect={setSelect}
						/> : 
						<StartMenu 
							show={start} 
							onStart={setStart} 
						/>
	);
};

export default App;
