import React, {Component} from 'react';
import { connect } from 'react-redux';

import NextQuestion from './NextQuestion';
import '../style/App.scss';

class QuestionDisplay extends Component {

	checkAnswer(event) {
		const choices = document.getElementsByClassName('choice');
		let buttonIndex = 0;
		for (let i = 0; i < choices.length; i++) {
			if (event.target.textContent === choices[i].textContent) {
				button = i;
				break;
			}
		}
		const answerIndex = questions[questionIndex].answer;
		if (event.target.textContent === questions[questionIndex].choices[answerIndex]) {
			refs.current[answerIndex].classList.add("choice-correct");
			onCorrect();
		}
		else {
			//need to find index of the event.target.textcontent ref - loop through refs
			refs.current[refIndex].classList.add("choice-incorrect");
			refs.current[answerIndex].classList.add("choice-correct");
		}
		onAnswerSelect();
	}

	// but {
	// 	event.preventDefault();
	// }

	renderChoices() {
		const choices = this.props.questions[this.props.questionIndex].choices.map((choice, index) => {
			return (
				<button
					dangerouslySetInnerHTML={{__html: choice}}
					className="choice"
					key={index}
					onClick={this.checkAnswer}
				>
				</button>
			);
		})
		return choices;
	}

	render() {
		return (
			<div className="container">
				<strong>Question: {this.props.questionIndex + 1} of {this.props.length}</strong>
				<div className="question-display">
					<div className="question-text">
						<h1 dangerouslySetInnerHTML={{__html:this.props.questions[this.props.questionIndex].question}}></h1>
					</div>
					<div className="choice-wrapper">
						{this.renderChoices()}
					</div>
				</div>
				<div className="next">
				</div>
			</div>
		);
	}
}
					// {select ? <NextQuestion next={next} questionIndex={questionIndex} length={length-1} /> : ''}

const mapStateToProps = state => {
	return {
		questionIndex: state.questionIndexReducer.questionIndex,
		questions: state.questionReducer.questions,
		select: state.selectReducer.select
	};
};

export default connect(mapStateToProps)(QuestionDisplay);

