import { combineReducers } from 'redux';

const questionReducer = (state=[], action) => {
    switch(action.type) {
        case 'FETCH_QUESTIONS':
            return action.payload.forEach(question => {
                const formattedQuestions = {
                    question: question.question
                }
                const answerChoices = [...question.incorrect_answers];
                formattedQuestions.answer = Math.floor(Math.random() * 3) + 1;
                answerChoices.splice(formattedQuestions.answer -1, 0, question.correct_answer);
                answerChoices.forEach((choice, index) => {
                    formattedQuestions["choice" + (index+1)] = choice;
                })
            });
        default:
            return state;
    }
};

export default combineReducers({questionReducer});
