import React, { Component } from 'react';
import { connect } from 'react-redux';

import StartMenu from './components/StartMenu';
import QuestionDisplay from './components/QuestionDisplay';
import {fetchQuestions, nextQuestion, resetSelect, endGame } from './actions';

class App extends Component {

	componentDidMount() {
		this.props.fetchQuestions();
}


	// setting up the next question - if last question has been reached reset the game
	nextQuestion = () => {
		this.props.nextQuestion();
		const buttons = document.getElementsByTagName('button');
		for (let i = 0; i < buttons.length; i++) {
			if (buttons[i].classList.contains('choice-correct')) {
				buttons[i].classList.remove('choice-correct')
			} else if (buttons[i].classList.contains('choice-incorrect')) {
				buttons[i].classList.remove('choice-incorrect')
			}
		}
		if (this.props.questionIndex < this.props.questions.length - 1) {
			this.props.resetSelect();
		} else {
			this.props.fetchQuestions();
			this.gameOver();
		}
	}

	// incrementScore = () => {
	// 	this.setState({score: this.state.score + 1});
	// }

	render() {
		// const { select, questionIndex, gameOver, score } = this.state;
		const { questions, start } = this.props;
		return (
			start ? <QuestionDisplay next={this.nextQuestion} /> : 
							<StartMenu 
								// score={score}
								// total={questions.length}
								// fetch={fetchQuestions}
							/>
		);
	}
}

const mapStateToProps = state => {
	return { 
		questions: state.questionReducer.questions,
		questionIndex: state.updateState.questionIndex,
		start: state.updateState.start 
	}
}

export default connect(mapStateToProps, {fetchQuestions, nextQuestion, resetSelect, endGame })(App);
