import React from 'react';

const StartMenu = ({show, onStart, gameOver, setQuestionNumber, questionNumber}) => {

	const restart = () => {
		setQuestionNumber(0);
		onStart(!show);
	}

	return (
		gameOver ? 
			<div>
				<button onClick={restart}>Play Again?</button>
			</div> :
			<div>
				<h1>Sports Trivia</h1>
				<div>
					<button onClick={() => onStart(!show)}>Start</button>
				</div>
			</div>
	);
};

export default StartMenu;
