import React from 'react';

const QuestionDisplay = ({display}) => {

	const checkAnswer = (event) => {
		for (let i = 0; i < display.questionChoices.length; i++) {
			if (event.target.textContent === display.questionChoices[i].answerText) {
				if (display.questionChoices[i].isCorrect === false) {
					console.log('you are wrong!');
				}
				else {
					console.log('you are right!');
				}
			}
		}
	}


	return (
		<div>
			<h1>{display.questionText}</h1>
			<div>
				<button onClick={() => console.log(display) }>{display.questionChoices[0].answerText}</button>
				<button>{display.questionChoices[1].answerText}</button>
				<button>{display.questionChoices[2].answerText}</button>
				<button>{display.questionChoices[3].answerText}</button>
			</div>
		</div>
	);
}

export default QuestionDisplay;
