import React, {Component} from 'react';
import { connect } from 'react-redux';
import { onChoiceSelect, onCorrect } from '../actions';

import NextQuestion from './NextQuestion';
import '../style/App.scss';

class QuestionDisplay extends Component {

	checkAnswer = event => {
		const choices = document.getElementsByClassName('choice');
		let buttonIndex = 0;
		for (let i = 0; i < choices.length; i++) {
			if (event.target.textContent === choices[i].textContent) {
				buttonIndex = i;
				break;
			}
		}
		const answerIndex = this.props.questions[this.props.questionIndex].answer;
		if (event.target.textContent === this.props.questions[this.props.questionIndex].choices[answerIndex]) {
			choices[answerIndex].classList.add("choice-correct");
			this.props.onCorrect();
		}
		else {
			//need to find index of the event.target.textcontent ref - loop through refs
			choices[buttonIndex].classList.add("choice-incorrect");
			choices[answerIndex].classList.add("choice-correct");
		}
		this.props.onChoiceSelect();
	}


	renderChoices() {
		const choices = this.props.questions[this.props.questionIndex].choices.map((choice, index) => {
			return (
				<button
					dangerouslySetInnerHTML={{__html: choice}}
					className="choice"
					key={index}
					onClick={this.checkAnswer}
				>
				</button>
			);
		})
		return choices;
	}

	render() {
		return (
			<div className="container">
				<strong>Question: {this.props.questionIndex + 1} of {this.props.questions.length}</strong>
				<div className="question-display">
					<div className="question-text">
						<h1 dangerouslySetInnerHTML={{__html:this.props.questions[this.props.questionIndex].question}}></h1>
					</div>
					<div className="choice-wrapper">
						{this.renderChoices()}
					</div>
				</div>
				<div className="next">
					{this.props.select ? <NextQuestion /> : ''}
				</div>
			</div>
		);
	}
}

const mapStateToProps = state => {
	return {
		questionIndex: state.updateState.questionIndex,
		questions: state.questionReducer.questions,
		select: state.updateState.select
	};
};

export default connect(mapStateToProps, {onChoiceSelect, onCorrect })(QuestionDisplay);
