import React from 'react';

const StartMenu = ({show, onStart, gameOver, setQuestionNumber, score, total}) => {

	return (
		gameOver ? 
			<div>
				<h2>You scored {score}/{total}</h2>
				<button onClick={() => onStart(!show)}>Play Again?</button>
			</div> :
			<div className="menu">
				<h1>Sports Trivia</h1>
				<div className="start">
					<button onClick={() => onStart(!show)}>Start</button>
				</div>
			</div>
	);
};

export default StartMenu;
