import React from 'react';
import './StartMenu.css';

const StartMenu = ({gameOver, score, total, startGame}) => {

//displaying user's score and giving them the option to restart the game
	return (
		gameOver ? 
			<div className="menu-end">
				<div className="score">
					<h2>You scored {score}/{total}</h2>
				</div>
				<div className="restart">
					<button onClick={startGame}>Play Again?</button>
				</div>
			</div> :
			<div className="menu">
				<div className="heading">
					<h1>General Knowledge Trivia</h1>
				</div>
				<div>
					<p>Put your skills to the test!</p>
				</div>
				<div className="start">
					<button onClick={startGame}>Start</button>
				</div>
			</div>
	);
};

export default StartMenu;
