import React, { Component } from 'react';
import { connect } from 'react-redux';
import { startGame } from '../actions';

class StartMenu extends Component {

//if gameOver is true display user's score and giving them the option to restart the game
//if false display the initial main menu with title and option to start the game
	render() {
		return (
			this.props.gameOver ? 
				<div className="menu-end">
					<div className="score">
						<h2>You scored {this.props.score}/{this.props.total}</h2>
					</div>
					<div className="restart">
						<button onClick={this.props.startGame}>Play Again?</button>
					</div>
				</div> :
				<div className="menu">
					<div className="heading">
						<h1>General Knowledge Trivia</h1>
					</div>
					<div className="start">
						<button onClick={this.props.startGame}>Start</button>
					</div>
				</div>
		);
	}
};

const mapStateToProps = state => {
	return {
		gameOver: state.updateState.gameOver,
		score: state.updateState.score
	}
}


export default connect(mapStateToProps, {startGame})(StartMenu);
