import React, { Component } from 'react';
import './StartMenu.css';

class StartMenu extends Component {

	//resetting the game - score and question number back to 0
	restart = () => {
		this.props.fetchQuestions();
		onStart(!show);
		resetScore(0);
		setQuestionNumber(0);
	}

	//displaying user's score and giving them the option to restart the game
	render() {
		const { fetchQuestions } = this.props;
		return (
			gameOver ? 
				<div className="menu-end">
					<div className="score">
						<h2>You scored {score}/{total}</h2>
					</div>
					<div className="restart">
						<button onClick={(restart)}>Play Again?</button>
					</div>
				</div> :
				<div className="menu">
					<div className="heading">
						<h1>Trivia</h1>
					</div>
					<div className="start">
						<button onClick={() => fetchQuestions}>Start</button>
					</div>
				</div>
		);
	}
};

const mapStateToProps = state => {
	return { questions: questionReducer }
}

export default connect(mapStateToProps, {fetchQuestions})(StartMenu);
