import React, { Component, useEffect, useRef } from 'react';
import { connect } from 'react-redux';

import { nextQuestion, endGame, resetSelect, fetchQuestions } from '../actions';


const NextQuestion = props => {

	const onNextClick = () => {
		const buttons = document.getElementsByTagName('button');
		for (let i = 0; i < buttons.length; i++) {
			if (buttons[i].classList.contains('choice-correct')) {
				buttons[i].classList.remove('choice-correct')
			} else if (buttons[i].classList.contains('choice-incorrect')) {
				buttons[i].classList.remove('choice-incorrect')
			}
		}
		props.nextQuestion();
	}


	useEffect(() => {
		if (props.questionIndex < props.questions.length - 1) {
			props.resetSelect();
			console.log(props.select);
		} else {
			props.fetchQuestions();
			props.endGame();
		}
	}, [props.questionIndex])

	//display the next button after user selects an answer
	//if on last question, button will display with text of 'finish' rather than 'next question'
	return (
		props.questionIndex === props.questions.length ? 
			<button onClick={onNextClick}>Finish</button> :
			<button onClick={onNextClick} >
				Next Question
			</button> 

	);
}

const mapStateToProps = state => {
	return {
		questionIndex: state.questionIndexReducer.questionIndex,
		questions: state.questionReducer.questions,
		select: state.selectReducer.select
	}
}

export default connect(mapStateToProps, { nextQuestion, resetSelect, endGame, fetchQuestions })(NextQuestion);
