import React, {useRef} from 'react';
import NextQuestion from './NextQuestion';
import './QuestionDisplay.css';

const QuestionDisplay = ({display, next, select, onAnswerSelect, onCorrect, gameOver}) => {
	const refs = useRef([]);

	const checkAnswer = event => {
		for (let i = 0; i < display.questionChoices.length; i++) {
			if (event.target.textContent === display.questionChoices[i].answerText) {
				if (display.questionChoices[i].isCorrect === false) {
					refs.current[i].setAttribute("style", "background-color: red");
				}
				else {
					refs.current[i].setAttribute("style", "background-color: green");
					onCorrect();
				}
			}
			onAnswerSelect(true);
			if (display.questionChoices[i].isCorrect === true) {
				refs.current[i].setAttribute("style", "background-color: green");
			}
		}
	}

	const prevent = event => {
		event.preventDefault();
	}

	const choices = display.questionChoices.map((choice, index) => {
		return (
			<button 
				className="choice"
				key={index}
				ref={el => refs.current.push(el)} 
				onClick={select ? prevent : checkAnswer}>{choice.answerText}
			</button>
		);
	})


	return (
		<div className="container">
			<div className="question-display">
				<div className="question-text">
					<h1>{display.questionText}</h1>
				</div>
				<div className="choice-wrapper">
					{choices}
				</div>
			</div>
			<div className="next">
				{select ? <NextQuestion next={next} questionNumber={questionNumber} /> : ''}
			</div>
		</div>
	);
}

export default QuestionDisplay;
