import React, {useRef} from 'react';
import NextQuestion from './NextQuestion';
import './QuestionDisplay.css';

const QuestionDisplay = ({questions, next, select, onCorrect, questionIndex, length}) => {
	const refs = useRef([]);

	//looping through answer choices and checking them against the user's selected choice
	const checkAnswer = event => {
		let refIndex = 0;
		for (let i = 0; i < refs.current.length; i++) {
			if (event.target.textContent === refs.current[i].textContent) {
				refIndex = i;
			}
		}
		const answerIndex = questions[questionIndex].answer;
		if (event.target.textContent === questions[questionIndex].choices[answerIndex]) {
			refs.current[answerIndex].setAttribute("style", "background-color: green");
			onCorrect();
		}
		else {
			//need to find index of the event.target.textcontent ref - loop through refs
			refs.current[refIndex].setAttribute("style", "background-color: red");
			refs.current[answerIndex].setAttribute("style", "background-color: green");
		}
	}

	const prevent = event => {
		event.preventDefault();
	}

	const choices = questions[questionIndex].choices.map((choice, index) => {
		return (
			<button 
				className="choice"
				key={index}
				ref={el => refs.current.push(el)} 
				onClick={select ? prevent : checkAnswer}>{choice}
			</button>
		);
	})
	return (
		<div className="container">
			<div className="question-display">
				<div className="question-text">
					<h1 dangerouslySetInnerHTML={questions[questionIndex].question}></h1>
				</div>
				<div className="choice-wrapper">
					{choices}
				</div>
			</div>
			<div className="next">
				{select ? <NextQuestion next={next} questionNumber={questionIndex} length={length-1} /> : ''}
			</div>
		</div>
	);
}

export default QuestionDisplay;
