import React, {Component} from 'react';
import { connect } from 'react-redux';
import { onChoiceSelect, onCorrect, endGame } from '../actions';

import NextQuestion from './NextQuestion';
import '../style/App.scss';

class QuestionDisplay extends Component {

	checkAnswer = event => {
		const choices = document.getElementsByClassName('choice');
		let buttonIndex = 0;
		for (let i = 0; i < choices.length; i++) {
			if (event.target.textContent === choices[i].textContent) {
				buttonIndex = i;
				break;
			}
		}
		const answerIndex = this.props.questions[this.props.questionIndex].answer;
		if (event.target.textContent === this.props.questions[this.props.questionIndex].choices[answerIndex]) {
			choices[answerIndex].classList.add("choice-correct");
			this.props.onCorrect();
		}
		else {
			//need to find index of the event.target.textcontent ref - loop through refs
			choices[buttonIndex].classList.add("choice-incorrect");
			choices[answerIndex].classList.add("choice-correct");
		}
		this.nextQuestion();
	}

	nextQuestion() {
		const buttons = document.getElementsByTagName('button');
		for (let i = 0; i < buttons.length; i++) {
			if (buttons[i].classList.contains('choice-correct')) {
				buttons[i].classList.remove('choice-correct')
			} else if (buttons[i].classList.contains('choice-incorrect')) {
				buttons[i].classList.remove('choice-incorrect')
			}
		}
		if (this.props.questionIndex < this.props.questions.length - 1) {
			this.props.onChoiceSelect();
		} else {
			this.props.fetchQuestions();
			this.props.endGame();
		}
	}

	renderChoices() {
		const choices = this.props.questions[this.props.questionIndex].choices.map((choice, index) => {
			return (
				<button
					dangerouslySetInnerHTML={{__html: choice}}
					className="choice"
					key={index}
					onClick={this.checkAnswer}
				>
				</button>
			);
		})
		return choices;
	}

	render() {
		return (
			<div className="container">
				<strong>Question: {this.props.questionIndex + 1} of {this.props.length}</strong>
				<div className="question-display">
					<div className="question-text">
						<h1 dangerouslySetInnerHTML={{__html:this.props.questions[this.props.questionIndex].question}}></h1>
					</div>
					<div className="choice-wrapper">
						{this.renderChoices()}
					</div>
				</div>
				<div className="next">
					{this.props.select ? <NextQuestion /> : ''}
				</div>
			</div>
		);
	}
}

const mapStateToProps = state => {
	return {
		questionIndex: state.questionIndexReducer.questionIndex,
		questions: state.questionReducer.questions,
		select: state.selectReducer.select
	};
};

export default connect(mapStateToProps, {onChoiceSelect, onCorrect, endGame })(QuestionDisplay);

